mapper.select:
SELECT sys_user.*,
       sys_user_extend.avatar,
       sys_user_extend.platform_info
FROM sys_user
         LEFT JOIN sys_user_extend
                   ON sys_user.id = sys_user_extend.id
WHERE sys_user.id = #{id}
   OR sys_user.phone = #{id}
   OR sys_user.email = #{id}

SELECT sr.id, sr.name, sur.user_id
FROM sys_role sr
inner join sys_user_role sur on sr.id = sur.role_id
WHERE sur.user_id IN

    #{userId}

and sr.organization_id = #{orgId}

SELECT sys_user.id, sys_user.name, sys_user.email, sys_user.phone
FROM sys_user LEFT JOIN sys_organization_user on sys_organization_user.user_id = sys_user.id
WHERE sys_organization_user.organization_id = #{organizationId}
AND sys_user.id IN

    #{id}

SELECT id, name
FROM sys_user
WHERE organization_id = #{orgId}

mapper.update:
UPDATE sys_user
SET password = CASE

    WHEN id = #{user.id} THEN #{user.password}

END
WHERE id IN

    #{user.id}

UPDATE sys_user
SET password = #{password}
WHERE id = #{id}

mapper.select:
select count(id) from sys_user


        and email = #{email}


        and id != #{id}

select count(id) from sys_user


        and phone = #{phone}


        and id != #{id}

SELECT distinct sou.resource_user_id
FROM sys_organization_user sou
WHERE sou.organization_id = #{orgId}
AND sou.user_id IN

    #{id}

mapper.update:
